import fetch from 'isomorphic-unfetch';
import { Charts, ChartContainer, ChartRow, YAxis, Resizable, BarChart } from "react-timeseries-charts";
import { TimeSeries, TimeRange, Index as PondIndex } from "pondjs";

const Index = (props: { data: any }) => {

  const data = props.data.map(e => [e[0], e[4]]);

const timeseries = new TimeSeries({
  name: "hilo_rainfall",
  columns: ["index", "precip"],
  points: data.map(([d, value]) => [
      (PondIndex.getIndexString as any)("1h", new Date(d)),
      value
  ])
});

  return <div>
    <Resizable>
      <ChartContainer timeRange={timeseries.timerange()} >
        <ChartRow height="150">
          <YAxis
            id="rain"
            label="Rainfall (inches/hr)"
            min={0}
            max={100}
            format=".2f"
            width="70"
            type="linear"
          />
          <Charts>
            <BarChart
              axis="rain"
              spacing={1}
              columns={["precip"]}
              series={timeseries}
            />
          </Charts>
        </ChartRow>
      </ChartContainer>
    </Resizable>
    {/* <p>{JSON.stringify(props.timeseries)}</p> */}
    <div>{JSON.stringify(props.data)}</div>
  </div>
}

Index.getInitialProps = async function () {
  const res = await fetch('https://raw.githubusercontent.com/nytimes/covid-19-data/master/us-counties.csv');
  // const data = await res.json();

  const text = await res.text();
  const [headings, ...data] = text.split('\n');
  const table = data.filter(e => e.includes('San Francisco')).map(e => e.split(','))

  const data2 = [
    ["2017-01-24T00:00", 0.01],
    ["2017-01-24T01:00", 0.13],
    ["2017-01-24T02:00", 0.07],
    ["2017-01-24T03:00", 0.04],
    ["2017-01-24T04:00", 0.33],
  ];


  // const timeseries = new TimeSeries(seriesData);

  return {
    data: table,
  };
};

export default Index;
