import fetch from 'isomorphic-unfetch';
import { Charts, ChartContainer, ChartRow, YAxis, Resizable, BarChart, styler } from "react-timeseries-charts";
import { TimeSeries, TimeRange, Index as PondIndex } from "pondjs";

const ChartForCity = (props: { data: any, city: string }) => {
  const city = props.data.filter(e => e.includes(props.city));
  const data = city.map(e => [e[0], e[4]]);

  const timeseries = new TimeSeries({
    name: "corona",
    columns: ["index", "cases"],
    points: data.map(([d, value]) => [
      (PondIndex.getIndexString as any)("1h", new Date(d)),
      value
    ])
  });

  const style = styler([
    { key: "cases", color: "#35C8E1"},
]);

  return <div>
    <h2>{props.city}</h2>
    <Resizable>
      <ChartContainer timeRange={timeseries.timerange()} >
        <ChartRow height="150">
          <YAxis
            id="cases"
            label="Coronavirus cases"
            min={0}
            max={timeseries.max('cases')}
            type="linear"
          />
          <Charts>
            <BarChart
              axis="cases"
              style={style}
              spacing={1}
              columns={["cases"]}
              series={timeseries}
            />
          </Charts>
        </ChartRow>
      </ChartContainer>
    </Resizable>
  </div>
}

const Index = (props: { data: any, lastUpdated: string }) => {
  return <>
    <div>
      Last updated: {props.lastUpdated}
    </div>
    <ChartForCity city='San Francisco' data={props.data} />
  </>
}

Index.getInitialProps = async function () {
  const res = await fetch('https://raw.githubusercontent.com/nytimes/covid-19-data/master/us-counties.csv');
  const lastUpdated = (new Date()).toLocaleDateString();

  const text = await res.text();
  const [headings, ...data] = text.split('\n');
  const table = data.map(e => e.split(','))

  return {
    data: table,
    lastUpdated,
  };
};

export default Index;
