import fetch from 'isomorphic-unfetch';
import { Charts, ChartContainer, ChartRow, YAxis, Resizable, BarChart } from "react-timeseries-charts";
import { TimeSeries, TimeRange, Index as PondIndex } from "pondjs";

const Index = (props: { data: any }) => {


const timeseries = new TimeSeries({
  name: "hilo_rainfall",
  columns: ["index", "precip"],
  points: props.data.map(([d, value]) => [
      (PondIndex.getIndexString as any)("1h", new Date(d)),
      value
  ])
});

  return <div>
    <Resizable>
      <ChartContainer timeRange={timeseries.timerange()} >
        <ChartRow height="150">
          <YAxis
            id="rain"
            label="Rainfall (inches/hr)"
            min={0}
            max={1.5}
            format=".2f"
            width="70"
            type="linear"
          />
          <Charts>
            <BarChart
              axis="rain"
              spacing={1}
              columns={["precip"]}
              series={timeseries}
            />
          </Charts>
        </ChartRow>
      </ChartContainer>
    </Resizable>
    {/* <p>{JSON.stringify(props.timeseries)}</p> */}
    <div>test</div>
  </div>
}

Index.getInitialProps = async function () {
  const res = await fetch('https://raw.githubusercontent.com/nytimes/covid-19-data/master/us-counties.csv');
  // const data = await res.json();

  const text = await res.text();
  const [headings, ...data] = text.split('\n');
  const table = data.filter(e => e.includes('San Francisco')).map(e => e.split(','))


  const seriesData = {
    name: "traffic",
    columns: ["time", "in", "out"],
    points: [
      [1400425947000, 52, 41],
      [1400425948000, 18, 45],
      [1400425949000, 26, 49],
      [1400425950000, 93, 81],
    ]
  };



  const data2 = [
    ["2017-01-24T00:00", 0.01],
    ["2017-01-24T01:00", 0.13],
    ["2017-01-24T02:00", 0.07],
    ["2017-01-24T03:00", 0.04],
    ["2017-01-24T04:00", 0.33],
    ["2017-01-24T05:00", 0.2],
    ["2017-01-24T06:00", 0.08],
    ["2017-01-24T07:00", 0.54],
    ["2017-01-24T08:00", 0.95],
    ["2017-01-24T09:00", 1.12],
    ["2017-01-24T10:00", 0.66],
    ["2017-01-24T11:00", 0.06],
    ["2017-01-24T12:00", 0.3],
    ["2017-01-24T13:00", 0.05],
    ["2017-01-24T14:00", 0.5],
    ["2017-01-24T15:00", 0.24],
    ["2017-01-24T16:00", 0.02],
    ["2017-01-24T17:00", 0.98],
    ["2017-01-24T18:00", 0.46],
    ["2017-01-24T19:00", 0.8],
    ["2017-01-24T20:00", 0.39],
    ["2017-01-24T21:00", 0.4],
    ["2017-01-24T22:00", 0.39],
    ["2017-01-24T23:00", 0.28]
];


console.log('series2', series2);

  // const timeseries = new TimeSeries(seriesData);

  return {
    data: data2,
  };
};

export default Index;
